function isoNow() { return new Date().toISOString(); }
function asIso(value) {
  if (!value) return isoNow();
  const parsed = Date.parse(value);
  return Number.isNaN(parsed) ? isoNow() : new Date(parsed).toISOString();
}

const topic = msg.topic || '';
const payload = (typeof msg.payload === 'object' && msg.payload) ? msg.payload : { raw: msg.payload };
let out = {
  source: 'unknown',
  deviceId: 'unknown',
  eventType: 'unknown',
  ts: isoNow(),
  topic,
  metrics: {},
  raw: payload
};

if (topic.startsWith('zigbee2mqtt/')) {
  const parts = topic.split('/');
  if (parts.length >= 2 && parts[1] !== 'bridge') {
    out.source = 'zigbee';
    out.deviceId = parts[1];
    out.eventType = 'state';
    out.metrics = payload;
    out.ts = asIso(payload.last_seen);
  }
}

if (topic.startsWith('application/')) {
  const parts = topic.split('/');
  if (parts.length >= 6 && parts[2] === 'device' && parts[4] === 'event') {
    out.source = 'lorawan';
    out.deviceId = parts[3];
    out.eventType = parts[5];
    out.metrics = payload;
    out.ts = asIso(payload.time);
  }
}

msg.poc = out;
return msg;

const p = msg.poc;
msg.query = `
  INSERT INTO poc.devices (network, external_id, display_name, meta, first_seen_at, last_seen_at)
  VALUES ($1, $2, $3, $4::jsonb, now(), $5)
  ON CONFLICT (network, external_id)
  DO UPDATE SET last_seen_at = EXCLUDED.last_seen_at,
                meta = poc.devices.meta || EXCLUDED.meta;
`;
const meta = {
  last_topic: p.topic,
  last_event_type: p.eventType,
  last_keys: Object.keys(p.metrics || {})
};
msg.params = [p.source, p.deviceId, p.deviceId, JSON.stringify(meta), p.ts];
return msg;

const p = msg.poc;
msg.query = `
  INSERT INTO poc.telemetry (device_id, ts, metrics, raw, source_topic)
  SELECT id, $1, $2::jsonb, $3::jsonb, $4
  FROM poc.devices
  WHERE network = $5 AND external_id = $6;
`;
msg.params = [p.ts, JSON.stringify(p.metrics || {}), JSON.stringify(p.raw || {}), p.topic, p.source, p.deviceId];

const key = `${p.source}:${p.deviceId}`;
const cache = flow.get('poc_last') || {};
cache[key] = p;
flow.set('poc_last', cache);

return msg;

const cache = flow.get('poc_last') || {};
msg.payload = { ts: new Date().toISOString(), count: Object.keys(cache).length, items: cache };
return msg;
